/* Checkbox container */
.checkbox-container {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  cursor: pointer;
  flex: 1;
}

/* Checkmark circle */
.checkmark {
  width: 24px;
  height: 24px;
  border: 2px solid #ccc;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  flex-shrink: 0;
}

.checkmark.checked {
  background: linear-gradient(135deg, #4ecdc4, #45b7d1);
  border-color: transparent;
  animation: checkBounce 0.5s cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

.checkmark-icon {
  color: white;
  font-weight: bold;
  font-size: 14px;
  animation: checkmarkAppear 0.3s ease-out forwards;
}

/* Todo text */
.todo-text {
  flex: 1;
  transition: all 0.3s ease;
}

.todo-list li.completed .todo-text {
  text-decoration: line-through;
  color: #888;
}

/* Bounce animation for checkmark */
@keyframes checkBounce {
  0% {
    transform: scale(0.8);
  }
  50% {
    transform: scale(1.2);
  }
  100% {
    transform: scale(1);
  }
}

/* Checkmark icon appear animation */
@keyframes checkmarkAppear {
  0% {
    opacity: 0;
    transform: scale(0) rotate(-45deg);
  }
  100% {
    opacity: 1;
    transform: scale(1) rotate(0deg);
  }
}

/* Pulse effect on hover */
.checkmark:hover {
  transform: scale(1.1);
  border-color: #4ecdc4;
}

/* Completed item row effect */
.todo-list li.completed {
  background-color: #f0fff0;
  animation: rowComplete 0.4s ease-out;
}

@keyframes rowComplete {
  0% {
    background-color: #e8f8f5;
  }
  50% {
    background-color: #d1f2eb;
  }
  100% {
    background-color: #f0fff0;
  }
}

